package basics;

public class Patter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

        for(int row =1 ;row<=4;row++)
        {
        	for(int col = 1;col<=4;col++)
        	{
        		System.out.print(col);
        	}
        	
        	System.out.println();
        }

		
	}

}
